      categoryInitOptions: [],
      categoryOptions: [],
      categorySelectedOptions: [],
      categoryTextField: "",
      categoryValue: "",
      id: "",
      fileURL: "",
      fileFlag: false,
      nameFlag: false,
      categoryFlag: false,
      activeDeleteModal: false,
      host: "8b430148.ngrok.io",
      supplierName: "",
      oilName: "",
      oilType: "",
      functionalSub: "",
      aromaticSub: "",
