import React from "react";
import axios from "axios";
import {
  Layout,
  Card,
  ResourceList,
  TextStyle,
  Icon,
  Thumbnail,
  Modal,
  Stack,
  DropZone,
  InlineError,
  TextField,
  Autocomplete
} from "@shopify/polaris";
import { DeleteMajorMonotone, EditMajorMonotone } from "@shopify/polaris-icons";

export default class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      allImageData: [],
      listItemCount: 0,
      imageListItems: [],
      selectedListItems: [],
      activeModal: false,
      modalAction: {
        content: "Add",
        onAction: this.handleAddImages
      },
      loading: false,
      file: null,
      validImageTypes: ["image/gif", "image/jpeg", "image/png"],
      openFileDialog: false,
      fileUpload: "",
      uploadedFile: "",
      fileError: "",
      imageNameError: "",
      imageCategoryError: "",
      imageName: "",
      categoryInitOptions: [],
      categoryOptions: [],
      categorySelectedOptions: [],
      categoryTextField: "",
      categoryValue: "",
      id: "",
      fileURL: "",
      fileFlag: false,
      nameFlag: false,
      categoryFlag: false,
      activeDeleteModal: false,
      host: "8b430148.ngrok.io",
      supplierName: "",
      oilName: "",
      oilType: "",
      functionalSub: "",
      aromaticSub: "",
      blendsWellWith: "",
      aromaticDescription: "",
      aromaType: "",
      classifications: "",
      note: "",
      supplierNameError: "",
      oilNameError: "",
      oilTypeError: "",
      functionalSubError: "",
      aromaticSubError: "",
      blendWellWithError: "",
      aromaticDescriptionError: "",
      aromaTypeError: "",
      classificationsError: "",
      noteError: ""
    };
  }

  async componentDidMount() {
    this.getList();
  }

  getList = async () => {
    try {
      let response = await axios.get("https://" + this.state.host + "/drop");
      this.setState({ allImageData: response.data });
      let allImageData = response.data;
      let allCategorys = [];
      for (let i in allImageData) {
        allCategorys.push(allImageData[i].category);
      }
      let filterCategory = allCategorys.filter(function(elem, index, self) {
        return index === self.indexOf(elem);
      });
      let categoryArray = [];
      for (let i in filterCategory) {
        let item = filterCategory[i];
        categoryArray.push({
          value: item,
          label: item
        });
      }
      this.setState({
        categoryInitOptions: categoryArray,
        categoryOptions: categoryArray
      });
      
      let imageListItemsData = [];
      for (let i in allImageData) {
        let item = allImageData[i];
        imageListItemsData.push({
          id: item._id,
          supplierName: item.supplierName,
          oilName: item.oilName,
          oilType: item.oilType,
          functionalSub: item.functionalSub,
          aromaticSub: item.aromaticSub,
          blendsWellWith: item.blendsWellWith,
          aromaticDescription: item.aromaticDescription,
          aromaType: item.aromaType,
          classifications: item.classifications,
          note: item.note,
          media: <Thumbnail source={item.filepath} alt={item.filename} />
        });
      }
      this.setState({
        imageListItems: imageListItemsData,
        listItemCount: imageListItemsData.length
      });
    } catch (error) {
      console.log(error);
    }
  };

  addNewImage = () => {
    this.setState({
      activeModal: true,
      file: null,
      fileURL: "",
      supplierName: "",
      oilName: "",
      oilType: "",
      functionalSub: "",
      aromaticSub: "",
      blendsWellWith: "",
      aromaticDescription: "",
      aromaType: "",
      classifications: "",
      note: "",
      fileError: "",
      supplierNameError: "",
      oilNameError: "",
      oilTypeError: "",
      functionalSubError: "",
      aromaticSubError: "",
      blendWellWithError: "",
      aromaticDescriptionError: "",
      aromaTypeError: "",
      classificationsError: "",
      noteError: "",
      modalAction: {
        content: "Add",
        onAction: this.handleAddImages
      }
    });
  };

  setSelectedListItems = items => {
    console.log(items);
  };

  editImage = (id, name, media, category) => {
    console.log(id, name, media, category);
    this.setState({
      modalAction: {
        content: "Save",
        onAction: this.handleEditImages
      },
      fileError: "",
      imageNameError: "",
      imageCategoryError: "",
      id: id,
      imageName: name,
      file: null,
      fileURL: media.props.source,
      categoryValue: category,
      activeModal: true,
      fileFlag: false,
      nameFlag: false,
      categoryFlag: false
    });
  };

  deleteImage = id => {
    this.setState({
      id: id,
      activeDeleteModal: true
    });
    console.log(id);
  };

  confirmDelete = async () => {
    this.setState({ loading: true });
    let response = await axios.get(
      `https://${this.state.host}/drop/deleteImage?id=${this.state.id}`
    );
    console.log(response.data);
    if (response.data === "success") {
      this.getList();
      this.setState({
        loading: false,
        activeDeleteModal: false
      });
    } else {
      console.log(response.data);
    }
  };

  closeModal = () => {
    this.setState({ activeModal: false });
  };

  handleDropZoneDrop = (_dropFiles, acceptedFiles, _rejectedFiles) => {
    this.setState({
      file: acceptedFiles[0],
      fileError: "",
      fileFlag: true,
      fileURL: ""
    });
  };

  toggleOpenFileDialog = () => {
    this.setState({ openFileDialog: !this.state.openFileDialog });
    console.log("toggle");
  };

  updateImageName = value => {
    this.setState({
      imageName: value,
      imageNameError: "",
      nameFlag: true
    });
  };

  updateCategoryOptions = selected => {
    const selectedValue = selected.map(selectedItem => {
      const matchedOption = this.state.categoryOptions.find(option => {
        return option.value.match(selectedItem);
      });
      return matchedOption && matchedOption.label;
    });
    this.setState({ categorySelectedOptions: [] });
    this.updateCategoryName(selectedValue[0]);
  };

  updateCategoryName = value => {
    this.setState({
      categoryValue: value,
      imageCategoryError: "",
      categoryFlag: true
    });
    if (this.state.categoryValue === "") {
      this.setState({ categorySelectedOptions: this.state.categoryOptions });
    }

    const filterRegex = new RegExp(value, "i");
    const resultOptions = this.state.categoryInitOptions.filter(option =>
      option.label.match(filterRegex)
    );
    this.setState({ categoryOptions: resultOptions });
  };

  updateSupplierName = value => {
    this.setState({
      supplierName: value,
      supplierNameError: ""
      // nameFlag: true
    });
  };

  updateOilName = value => {
    this.setState({
      oilName: value,
      oilNameError: ""
      // nameFlag: true
    });
  };
  updateOilType = value => {
    this.setState({
      oilType: value,
      oilTypeError: ""
      // nameFlag: true
    });
  };
  updateFunctionalSub = value => {
    this.setState({
      functionalSub: value,
      functionalSubError: ""
      // nameFlag: true
    });
  };
  updateAromaticSub = value => {
    this.setState({
      aromaticSub: value,
      aromaticSubError: ""
      // nameFlag: true
    });
  };
  updateBlendWellWith = value => {
    this.setState({
      blendsWellWith: value,
      blendWellWithError: ""
      // nameFlag: true
    });
  };
  updateAromaticDescription = value => {
    this.setState({
      aromaticDescription: value,
      aromaticDescriptionError: ""
      // nameFlag: true
    });
  };
  updateAromaType = value => {
    this.setState({
      aromaType: value,
      aromaTypeError: ""
      // nameFlag: true
    });
  };
  updateClassifications = value => {
    this.setState({
      classifications: value,
      classificationsError: ""
      // nameFlag: true
    });
  };
  updateNote = value => {
    this.setState({
      note: value,
      noteError: ""
      // nameFlag: true
    });
  };

  handleAddImages = () => {
    console.log(
      this.state.file,
      this.state.imageName,
      this.state.categoryValue
    );
    if (
      this.state.file === null &&
      this.state.supplierName === "" &&
      this.state.oilName === "" &&
      this.state.oilType === "" &&
      this.state.aromaType === "" &&
      this.state.classifications === "" &&
      this.state.note === ""
    ) {
      this.setState({
        fileError: "Need Image",
        supplierNameError: "Need Supplier Name",
        oilNameError: "Need Oil Name",
        oilTypeError: "Need Oil Type",
        aromaTypeError: "Need Aroma Type",
        classificationsError: "Need Classifications",
        noteError: "Need Note"
      });
      return;
    } else if (this.state.file === null) {
      this.setState({
        fileError: "Need Image"
      });
      return;
    } else if (this.state.supplierName === "") {
      this.setState({
        supplierNameError: "Need Supplier Name"
      });
      return;
    } else if (this.state.oilName === "") {
      this.setState({
        oilNameError: "Need Oil Name"
      });
      return;
    } else if (this.state.oilType === "") {
      this.setState({
        oilTypeError: "Need Oil Type"
      });
      return;
    } else if (this.state.aromaType === "") {
      this.setState({
        aromaTypeError: "Need Aroma Type"
      });
      return;
    } else if (this.state.classifications === "") {
      this.setState({
        classificationsError: "Need Classifications"
      });
      return;
    } else if (this.state.note === "") {
      this.setState({
        noteError: "Need Note"
      });
      return;
    }
    for (let i in this.state.allImageData) {
      let item = this.state.allImageData[i];
      if (item.filename === this.state.oilName) {
        this.setState({
          oilNameError: "Oil Name Exist"
        });
        return;
      }
    }

    this.setState({ loading: true });
    let formdata = new FormData();
    formdata.append("file", this.state.file);
    formdata.append("supplierName", this.state.supplierName);
    formdata.append("oilName", this.state.oilName);
    formdata.append("oilType", this.state.oilType);
    formdata.append("functionalSub", this.state.functionalSub);
    formdata.append("aromaticSub", this.state.aromaticSub);
    formdata.append("blendsWellWith", this.state.blendsWellWith);
    formdata.append("aromaticDescription", this.state.aromaticDescription);
    formdata.append("aromaType", this.state.aromaType);
    formdata.append("classifications", this.state.classifications);
    formdata.append("note", this.state.note);

    axios
      .post("https://" + this.state.host + "/drop", formdata, {
        headers: {
          "Content-Type": "multipart/form-data"
        }
      })
      .then(res => {
        if (res.data === "success") {
          this.setState({ activeModal: false });
          this.getList();
          this.setState({ loading: false });
          console.log(res.data);
        } else {
          console.log(res.data);
        }
      });
  };

  handleEditImages = () => {
    if (this.state.imageName === "" && this.state.categoryValue === "") {
      this.setState({
        imageNameError: "Need Image Name",
        imageCategoryError: "Need Category"
      });
      return;
    } else if (this.state.imageName === "") {
      this.setState({
        imageNameError: "Need Image Name"
      });
      return;
    } else if (this.state.categoryValue === "") {
      this.setState({
        imageCategoryError: "Need Category"
      });
      return;
    }
    for (let i in this.state.allImageData) {
      let item = this.state.allImageData[i];
      if (item.filename === this.state.imageName) {
        if (this.state.nameFlag) {
          this.setState({
            imageNameError: "Image Name Exist"
          });
          return;
        }
      }
    }
    if (
      !this.state.fileFlag &&
      !this.state.nameFlag &&
      !this.state.categoryFlag
    ) {
      this.setState({ activeModal: false });
      return;
    }

    if (this.state.fileFlag) {
      this.setState({ loading: true });
      let formdata = new FormData();
      formdata.append("file", this.state.file);
      formdata.append("filename", this.state.imageName);
      formdata.append("category", this.state.categoryValue);
      formdata.append("id", this.state.id);
      axios
        .post("https://" + this.state.host + "/drop/editWithFile", formdata, {
          headers: {
            "Content-Type": "multipart/form-data"
          }
        })
        .then(res => {
          if (res.data === "success") {
            this.getList();
            this.setState({
              activeModal: false,
              loading: false
            });
            console.log(res.data);
          } else {
            console.log(res.data);
          }
        });
    } else {
      if (this.state.nameFlag) {
        console.log("edit both");
        this.setState({ loading: true });
        let formdata = {
          id: this.state.id,
          filename: this.state.imageName,
          category: this.state.categoryValue
        };
        axios
          .post("https://" + this.state.host + "/drop/editWithoutFile", {
            data: formdata
          })
          .then(res => {
            if (res.data === "success") {
              this.getList();
              this.setState({
                activeModal: false,
                loading: false
              });
              console.log(res.data);
            } else {
              console.log(res.data);
            }
          });
      } else if (this.state.categoryFlag) {
        console.log("edit Category");
        this.setState({ loading: true });
        let formdata = {
          id: this.state.id,
          category: this.state.categoryValue
        };
        axios
          .post("https://" + this.state.host + "/drop/editWithoutFile", {
            data: formdata
          })
          .then(res => {
            if (res.data === "success") {
              this.getList();
              this.setState({
                activeModal: false,
                loading: false
              });
              console.log(res.data);
            } else {
              console.log(res.data);
            }
          });
      }
    }
  };

  render() {
    const categoryTextField = (
      <Autocomplete.TextField
        onChange={this.updateCategoryName}
        label="Image Category"
        value={this.state.categoryValue}
        placeholder="Enter category"
        error={this.state.imageCategoryError}
      />
    );
    const fileUpload =
      this.state.fileURL === "" ? (
        !this.state.file && <DropZone.FileUpload />
      ) : (
        <Stack>
          <Thumbnail size="large" alt="edit" source={this.state.fileURL} />
        </Stack>
      );

    const uploadedFile = this.state.file && (
      <Stack>
        <Thumbnail
          size="large"
          alt={this.state.file.name}
          source={
            this.state.validImageTypes.indexOf(this.state.file.type) > 0
              ? window.URL.createObjectURL(this.state.file)
              : "https://cdn.shopify.com/s/files/1/0757/9955/files/New_Post.png?12678548500147524304"
          }
        />
      </Stack>
    );

    return (
      <div>
        <Layout>
          <Layout.Section>
            <Card
              title="Drop Images"
              actions={[
                {
                  content: "Add Drop Image",
                  onAction: this.addNewImage
                }
              ]}
            >
              <Card.Section>
                <TextStyle variation="subdued">
                  {this.state.listItemCount} Drop Images
                </TextStyle>
              </Card.Section>
              <Card.Section>
                <ResourceList
                  resourceName={{ singular: "image", plural: "images" }}
                  items={this.state.imageListItems}
                  selectedItems={this.state.selectedListItems}
                  onSelectionChange={this.setSelectedListItems}
                  renderItem={item => {
                    const { id, supplierName, media, oilName, oilType, functionalSub, aromaticSub, blendsWellWith, aromaticDescription, aromaType, classifications, note } = item;
                    const shortcutActions = [
                      {
                        content: (
                          <Icon source={EditMajorMonotone} color="red" />
                        ),
                        onAction: () =>
                          this.editImage(id, supplierName, media, oilName, oilType, functionalSub, aromaticSub, blendsWellWith, aromaticDescription, aromaType, classifications, note)
                      },
                      {
                        content: (
                          <Icon source={DeleteMajorMonotone} color="red" />
                        ),
                        onAction: () => this.deleteImage(id)
                      }
                    ];

                    return (
                      <ResourceList.Item
                        id={id}
                        media={media}
                        accessibilityLabel={`View details for ${name}`}
                        shortcutActions={shortcutActions}
                        persistActions
                      >
                        <h3>
                          <TextStyle variation="strong">
                            <span style={{ fontSize: 12 }}>Supplier Name: </span>
                            {supplierName}
                          </TextStyle>
                        </h3>
                        <h3>
                          <TextStyle variation="strong">
                            <span style={{ fontSize: 12 }}>Oil Name: </span>
                            {oilName}
                          </TextStyle>
                        </h3>
                        <h3>
                          <TextStyle variation="strong">
                            <span style={{ fontSize: 12 }}>Oil Type: </span>
                            {oilType}
                          </TextStyle>
                        </h3>
                        <h3>
                          <TextStyle variation="strong">
                            <span style={{ fontSize: 12 }}>Functional Sub: </span>
                            {functionalSub}
                          </TextStyle>
                        </h3>
                        <h3>
                          <TextStyle variation="strong">
                            <span style={{ fontSize: 12 }}>Aromatic Sub: </span>
                            {aromaticSub}
                          </TextStyle>
                        </h3>
                        <h3>
                          <TextStyle variation="strong">
                            <span style={{ fontSize: 12 }}>Blends Well With: </span>
                            {blendsWellWith}
                          </TextStyle>
                        </h3>
                        <h3>
                          <TextStyle variation="strong">
                            <span style={{ fontSize: 12 }}>Aromatic Description: </span>
                            {aromaticDescription}
                          </TextStyle>
                        </h3>
                        <h3>
                          <TextStyle variation="strong">
                            <span style={{ fontSize: 12 }}>Aroma Type: </span>
                            {aromaType}
                          </TextStyle>
                        </h3>
                        <h3>
                          <TextStyle variation="strong">
                            <span style={{ fontSize: 12 }}>Classifications: </span>
                            {classifications}
                          </TextStyle>
                        </h3>
                        <h3>
                          <TextStyle variation="strong">
                            <span style={{ fontSize: 12 }}>Note: </span>
                            {note}
                          </TextStyle>
                        </h3>
                      </ResourceList.Item>
                    );
                  }}
                />
              </Card.Section>
            </Card>
          </Layout.Section>
        </Layout>
        <Modal
          open={this.state.activeModal}
          onClose={this.closeModal}
          title="Add Image"
          primaryAction={this.state.modalAction}
          style={{ padding: "20px" }}
          loading={this.state.loading}
        >
          <Modal.Section>
            <Stack alignment="center">
              <Stack.Item>
                <div style={{ width: 100, height: 100 }}>
                  <DropZone
                    openFileDialog={this.state.openFileDialog}
                    onDrop={this.handleDropZoneDrop}
                    onFileDialogClose={this.toggleOpenFileDialog}
                  >
                    {fileUpload}
                    {uploadedFile}
                  </DropZone>
                  <InlineError message={this.state.fileError} />
                </div>
              </Stack.Item>
              <Stack.Item>
                <TextField
                  label="Supplier Name"
                  value={this.state.supplierName}
                  onChange={this.updateSupplierName}
                  placeholder="Enter Supplier Name"
                  error={this.state.supplierNameError}
                />
              </Stack.Item>
              <Stack.Item>
                <TextField
                  label="Oil Name"
                  value={this.state.oilName}
                  onChange={this.updateOilName}
                  placeholder="Enter Oil Name"
                  error={this.state.oilNameError}
                />
              </Stack.Item>
              <Stack.Item>
                <TextField
                  label="Oil Type"
                  value={this.state.oilType}
                  onChange={this.updateOilType}
                  placeholder="Enter Oil Type"
                  error={this.state.oilTypeError}
                />
              </Stack.Item>
              <Stack.Item>
                <TextField
                  label="Functional Sub"
                  value={this.state.functionalSub}
                  onChange={this.updateFunctionalSub}
                  placeholder="Enter Functional Sub"
                  error={this.state.functionalSubError}
                />
              </Stack.Item>
              <Stack.Item>
                <TextField
                  label="Aromatic Sub"
                  value={this.state.aromaticSub}
                  onChange={this.updateAromaticSub}
                  placeholder="Enter Aromatic Sub"
                  error={this.state.aromaticSubError}
                />
              </Stack.Item>
              <Stack.Item>
                <TextField
                  label="Blends well with"
                  value={this.state.blendsWellWith}
                  onChange={this.updateBlendWellWith}
                  placeholder="Enter Blend well with"
                  error={this.state.blendWellWithError}
                />
              </Stack.Item>
              <Stack.Item>
                <TextField
                  label="Aromatic Descriptions"
                  value={this.state.aromaticDescription}
                  onChange={this.updateAromaticDescription}
                  placeholder="Enter Aromatic Description"
                  error={this.state.aromaticDescriptionError}
                />
              </Stack.Item>
              <Stack.Item>
                <TextField
                  label="Aroma Type"
                  value={this.state.aromaType}
                  onChange={this.updateAromaType}
                  placeholder="Enter Aroma Type"
                  error={this.state.aromaTypeError}
                />
              </Stack.Item>
              <Stack.Item>
                <TextField
                  label="Classifications"
                  value={this.state.classifications}
                  onChange={this.updateClassifications}
                  placeholder="Enter Classifications"
                  error={this.state.imageNameError}
                />
              </Stack.Item>
              <Stack.Item>
                <TextField
                  label="Note"
                  value={this.state.note}
                  onChange={this.updateNote}
                  placeholder="Enter Note"
                  error={this.state.noteError}
                />
              </Stack.Item>
              {/* <Stack.Item>
                <TextField
                  label="Image Name"
                  value={this.state.imageName}
                  onChange={this.updateImageName}
                  placeholder="Enter image name"
                  error={this.state.imageNameError}
                />
              </Stack.Item>
              <Stack.Item>
                <Autocomplete
                  options={this.state.categoryOptions}
                  selected={this.state.categorySelectedOptions}
                  onSelect={this.updateCategoryOptions}
                  textField={categoryTextField}
                />
              </Stack.Item> */}
            </Stack>
          </Modal.Section>
        </Modal>
        <Modal
          open={this.state.activeDeleteModal}
          onClose={this.handleDeleteModalChange}
          title="Do you really want to delete?"
          loading={this.state.loading}
          primaryAction={{
            content: "Yes",
            onAction: this.confirmDelete
          }}
          secondaryActions={{
            content: "No",
            onAction: () => {
              this.setState({ activeDeleteModal: false });
            }
          }}
        />
      </div>
    );
  }
}
