import React, { useCallback, useState, useEffect, useRef } from "react";
import axios from "axios";
import {
  Modal,
  Autocomplete,
  InlineError,
  Stack,
  DropZone,
  TextField,
  Layout,
  Card,
  TextStyle,
  ResourceList,
  Thumbnail
} from "@shopify/polaris";

export default function Background() {

  //Get Image Data
  const [flag, setFlag] = useState(0)
  const [backgroundData, setBackgroundData] = useState([])
  const [backgroundCategory, setBackgroundCategory] = useState([])
  const [imageItems, setimageItems] = useState([])
  
  useEffect(() => {
    async function fetchData() {
      try {
        if (!flag) {
          let response = await axios.get('http://localhost:3000/background');
          setBackgroundData(response.data);
          let allCategorys = [];
          for (let i in backgroundData) {
            allCategorys.push(backgroundData[i].category);
          }
          let filterCategory = allCategorys.filter(function(elem, index, self) {
            return index === self.indexOf(elem)
          });
          let categoryArr = []
          for(let i in filterCategory) {
            let item = filterCategory[i];
            categoryArr.push({
              value: item,
              label: item
            })
          }
          setBackgroundCategory(categoryArr)
          let imageItemsData=[];
          for (let i in backgroundData) {
            let item = backgroundData[i];
            imageItemsData.push({
              id: item._id,
              name: item.filename,
              category: item.category,
              media: <Thumbnail source={item.filepath} alt={item.filename} />
            });
          }
          setimageItems(imageItemsData);
          setFlag(1);
        }
      } catch (e) {
        console.log(`Axios request failed: ${e}`);
      }
    }
    fetchData();
  });

   
  // Modal
  const [loading, setLoding] = useState(false);
  const [activeModal, setActiveModal] = useState(false);
  const handleChange = useCallback(() => setActiveModal(!activeModal), [
    activeModal
  ]);

  const [selectedItems, setSelectedItems] = useState([]);

  const editImages = () => {
    console.log(selectedItems)
    console.log(selectedItems)
  }

  const promotedBulkActions = [
    {
      content: 'Edit Images',
      onAction: editImages,
    },
    {
      content: 'Delete Images',
      onAction: handleChange,
    },
  ];

  const bulkActions = [
    {
      content: 'Add New Image',
      onAction: handleChange,
    },
  ];

  //Modal Upload File
  const [openFileDialog, setOpenFileDialog] = useState(false);
  const [file, setFile] = useState();
  const [imageError, setImageError] = useState("");
  function handleImageError(txt) {
    setImageError(txt);
  }
  const toggleOpenFileDialog = useCallback(
    () => setOpenFileDialog(openFileDialog => !openFileDialog),
    []
  );
  const validImageTypes = ["image/gif", "image/jpeg", "image/png"];
  const handleDropZoneDrop = useCallback(
    (_dropFiles, acceptedFiles, _rejectedFiles) => {
      setFile(file => acceptedFiles[0]);
      setImageError("");
    },
    []
  );
  const fileUpload = !file && <DropZone.FileUpload />;
  const uploadedFile = file && (
    <Stack>
      <Thumbnail
        size="large"
        alt={file.name}
        source={
          validImageTypes.indexOf(file.type) > 0
            ? window.URL.createObjectURL(file)
            : "https://cdn.shopify.com/s/files/1/0757/9955/files/New_Post.png?12678548500147524304"
        }
      />
    </Stack>
  );

  //Modal Image Name
  const [imageName, setimageName] = useState("");
  const [imageNameError, setImageNameError] = useState("");
  const handleImageName = useCallback(newValue => {
    setimageName(newValue);
    handleImageNameError("");
  }, []);
  function handleImageNameError(txt) {
    setImageNameError(txt);
  }

  //Modal Category
  const [selectedOptions, setSelectedOptions] = useState([]);
  const [categoryValue, setCategoryValue] = useState("");
  const [options, setOptions] = useState(backgroundCategory);
  const [imageCategoryError, setCategoryError] = useState("");

  function handleCategoryError(txt) {
    setCategoryError(txt);
  }
  const updateText = useCallback(
    value => {
      setCategoryValue(value);
      if (value === "") {
        setOptions(backgroundCategory);
        return;
      }
      try {
        const filterRegex = new RegExp(value, "i");
        const resultOptions = backgroundCategory.filter(option =>
          option.label.match(filterRegex)
        );
        setOptions(resultOptions);
      }
      catch(err) {
        console.log(err.message);
      }
      setCategoryError("");
    },
    [backgroundCategory]
  );

  const updateSelection = useCallback(selected => {
    updateText(selected[0]);
    setSelectedOptions(selected);
    setCategoryValue(selected[0]);
  }, []);

  const textField = (
    <Autocomplete.TextField
      onChange={updateText}
      label="Image Category"
      value={categoryValue}
      placeholder="Enter category"
      error={imageCategoryError}
    />
  );

  //Submit Image Data
  const handleImages = () => {
    if (file === undefined && imageName === "" && categoryValue === "") {
      handleImageError("Need Image");
      handleImageNameError("Need Image Name");
      handleCategoryError("Need Category");
      return;
    } else if (file === undefined && imageName === "") {
      handleImageError("Need Image");
      handleImageNameError("Need Image Name");
    } else if (file === undefined && categoryValue === "") {
      handleImageError("Need Image");
      handleCategoryError("Need Category");
    } else if (imageName === "" && categoryValue === "") {
      handleImageNameError("Need Image Name");
      handleCategoryError("Need Category");
    } else if (file === undefined) {
      handleImageError("Need Image");
      return;
    } else if (imageName === "") {
      handleImageNameError("Need Image Name");
      return;
    } else if (categoryValue === "") {
      handleCategoryError("Need Category");
      return;
    }
    setLoding(true)
    for (let i in backgroundData) {
      let item = backgroundData[i];
      // console.log(item)
      if(item.filename === imageName){
        handleImageNameError("Image Name Exist");
        return;
      }
    }

    let formdata = new FormData();
    formdata.append("file", file);
    formdata.append("filename", imageName);
    formdata.append("category", categoryValue);
    axios
      .post("http://localhost:3000/background", formdata, {
        headers: {
          "Content-Type": "multipart/form-data"
        }
      })
      .then(res => {
        if (res.data === "success") {
          setFlag(0)
          setActiveModal(false);
          setFile();
          setimageName('');
          setCategoryValue('');
          setLoding(false)
          console.log(res.data)
        } else {
          console.log(res.data);
        }
      });
  };

  return (
    <div>
      <Layout>
        <Layout.Section>
          <Card
            title="Background Images"
            actions={[
              {
                content: "Add Background Image",
                onAction: handleChange
              }
            ]}
          >
            <Card.Section>
              <ResourceList
                resourceName={{ singular: "image", plural: "images" }}
                items={imageItems}
                selectedItems={selectedItems}
                onSelectionChange={setSelectedItems}
                promotedBulkActions={promotedBulkActions}
                bulkActions={bulkActions}
                renderItem={item => {
                  const { id, name, media, category } = item;

                  return (
                    <ResourceList.Item
                      id={id}
                      media={media}
                      accessibilityLabel={`View details for ${name}`}
                    >
                      <h3>
                        <TextStyle variation="strong">
                          <span style={{ fontSize: 12 }}>Name: </span>
                          {name}
                        </TextStyle>
                      </h3>
                      <h3>
                        <TextStyle variation="strong">
                          <span style={{ fontSize: 12 }}>Category: </span>
                          {category}
                        </TextStyle>
                      </h3>
                    </ResourceList.Item>
                  );
                }}
              />
            </Card.Section>
          </Card>
        </Layout.Section>
      </Layout>
      <Modal
        open={activeModal}
        onClose={handleChange}
        title="Add Image"
        primaryAction={{
          content: "Add",
          onAction: handleImages
        }}
        style={{ padding: "20px" }}
        loading={loading}
      >
        <Modal.Section>
          <Stack alignment="center">
            <Stack.Item>
              <div style={{ width: 100, height: 100 }}>
                <DropZone
                  openFileDialog={openFileDialog}
                  onDrop={handleDropZoneDrop}
                  onFileDialogClose={toggleOpenFileDialog}
                >
                  {fileUpload}
                  {uploadedFile}
                </DropZone>
                <InlineError message={imageError} />
              </div>
            </Stack.Item>
            <Stack.Item>
              <TextField
                label="Image Name"
                value={imageName}
                onChange={handleImageName}
                placeholder="Enter image name"
                error={imageNameError}
              />
            </Stack.Item>
            <Stack.Item>
              <Autocomplete
                options={options}
                selected={selectedOptions}
                onSelect={updateSelection}
                textField={textField}
              />
            </Stack.Item>
          </Stack>
        </Modal.Section>
      </Modal>
    </div>
  );
}
